package artificial.intelligence.assignment.one.exception;

public class NoImageLoadException extends Exception {

	/**
	 * Auto-generated serial id
	 */
	private static final long serialVersionUID = -2347042853135446757L;

	public NoImageLoadException() {
		super();
	}

	public NoImageLoadException(String message, Throwable cause) {
		super(message, cause);
	}

	public NoImageLoadException(String message) {
		super(message);
	}

	public NoImageLoadException(Throwable cause) {
		super(cause);
	}

}
